package oops_p;

public class Stud {
	String name;
	int a,b,c;
	public Stud(String name, int a, int b, int c) {
		super();
		this.name = name;
		this.a = a;
		this.b = b;
		this.c = c;
	}
	public Stud() {
		// TODO Auto-generated constructor stub
	}
	@Override
	public String toString() {
		return "Stud [name=" + name + ", a=" + a + ", b=" + b + ", c=" + c + "]";
	}
	
	
}
